
public class TablaNot {
	static void tabla() {
		boolean x = true;
		boolean y = false;
		boolean a1 = !x;
		boolean a2 = !y;
		
		System.out.println("Tabla de verdad del NOT");
		System.out.println("NOT " + x + " = " + a1);
		System.out.println("NOT " + y + " = " + a2);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		tabla();
		System.out.println("");
	}

}
